  //     console.log(response);
  //     const messag = response.data;
  //     setMessages(messag);
  // })
  // .catch((err) => console.log(err));
  return (
    <Fragment>
      <ul>
        {messages.map(item => (
          <li key={item._id}>
            <p>{item.from}</p>
            <p>{item.text}</p>
          </li>
        ))}
      </ul>
    </Fragment>
  )
}

export default Mails
