import React, { Fragment, useState, useEffect } from 'react';
import axios from "axios";
import Header from '../components/header';

const Mails = () => {

  const [messages, setMessages] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        `${process.env.REACT_APP_API_URL}api/user/contact/all`
      );
      setMessages(result.data)
      console.log("updated");
    }
    fetchData();
    console.log("mounted");
  }, [])

  // axios.get(`${process.env.REACT_APP_API_URL}api/user/contact/all`)
  // .then(response => {
  //     console.log(response);
  //     const messag = response.data;
  //     setMessages(messag);
  // })
  // .catch((err) => console.log(err));
  return (
    <div className='container mt-5'>
      <Header />
      <Fragment>
        <div className='row'>
          {messages.map(item => (
            <div key={item._id} className="col-lg-3" style={{backgroundColor: '#c0c0c0',color: 'black',borderRadius: '3%',margin: '2px'}}>
              <div>
                <p>{item.name}</p>
              </div>
              <div>
                <p>{item.from}</p>
              </div>
              <div>
                <p>{item.subject}</p>
              </div>
              <div>
                <p>{item.text}</p>
              </div>
            </div>
          ))}
        </div>
      </Fragment>
    </div>
  )
}

export default Mails
